    db.collection("events").get().then(items => {
      console.log(items);
      const itemDocs = items.docs;
      const data = itemDocs.map(doc => doc.data());
      setItems(data);
    console.log(data);
    })
  }, [])

  useEffect(() => {
  }, [])

  console.log(items);
  return 
    items;
  
}
